package com.widgetfiles

import android.content.Context
import android.location.Location
import com.google.android.gms.location.LocationServices
import com.google.android.gms.tasks.Task

object LocationHelper {
 /**
 * Fetch the user's last known location as a Task<Location> from the fused location provider.
 * NOTE: Permissions MUST be granted before calling this, or it will fail with a security exception.
 * Usage: Call from an Activity/ViewModel/Service after ensuring location permission is granted.
 */
 @Suppress("MissingPermission")
 fun getUserLocation(context: Context): Task<Location> {
 val fusedLocationClient = LocationServices.getFusedLocationProviderClient(context)
 return fusedLocationClient.lastLocation
 }
}

